mapper.select:
select reply_no ,board_no ,reply ,replyer ,reply_date from (select /*+ INDEX(r reply_pk) */ rownum rn, r.* from tbl_reply r where board_no = #{bno}
and rownum <= (#{rpage} * 5) ) b
where b.rn > (#{rpage} - 1) * 5


 select /*+ INDEX(r reply_pk) */ r.*
       from tbl_reply r
       where board_no = #{bno}

mapper.insert:

  select reply_seq.nextval
  from dual


insert into tbl_reply (reply_no
                      ,board_no
                      ,reply
